import React, { useState, useEffect } from "react";

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = [
    "<",
    ">",
    '"',
    ":",
    ";",
    "'",
    "?",
    "(",
    ")",
    "&",
    "$",
    "@",
    "#",
    "~",
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey =
          availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: Math.random() * 100 + "%",
          animationDuration: Math.random() * 5 + 3 + "s", // Random animation duration between 3s to 8s
          animationDelay: Math.random() * 2 + "s", // Random animation delay between 0s to 2s
        };
        newKeys.push(newKey);
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);

      // Clear keys after they fall off the screen
      setTimeout(() => {
        setKeys((prevKeys) =>
          prevKeys.filter((key) => key.animationDelay !== "0s")
        );
      }, 8000); // Adjust timing as needed
    }, 3000); // Repeat every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={containerStyle}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            ...keyStyle,
            left: key.left,
            animationDuration: key.animationDuration,
            animationDelay: key.animationDelay,
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

// Inline styles
const containerStyle = {
  position: "relative",
  width: "100%",
  height: "100vh",
  overflow: "hidden",
  backgroundColor: "#f0f0f0", // Set your desired background color
};

const keyStyle = {
  position: "absolute",
  fontSize: "24px", // Adjust font size as needed
  color: "#333", // Set text color
  animationName: "fall",
  animationTimingFunction: "ease-in-out",
  animationIterationCount: "infinite",
};

export default FallingKeysComponent;
