import React, { useState, useEffect } from 'react';

const CodingKeysComponent = () => {
  const [codingKeys, setCodingKeys] = useState([]);
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Array of coding keys
    const keys = ['<', '>', '"', ':', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~'];

    // Function to generate random positions for animation
    const randomPosition = () => Math.random() * 100;

    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    // Generate new set of coding keys every 8 seconds
    const generateKeysInterval = setInterval(() => {
      const newKeys = keys.map((key, index) => {
        const initialX = randomPosition();
        const initialY = randomPosition();
        const duration = Math.random() * 10 + 10; // Random duration between 10 to 20 seconds
        const delay = Math.random() * 4; // Random delay between 0 to 4 seconds

        return (
          <div
            key={`${key}-${index}`}
            className="coding-key"
            style={{
              opacity: showKeys ? 1 : 0,
              animation: `moveKeys${index} ${duration}s linear ${delay}s forwards`, // Use forwards to retain final animation state
              transform: `translate(${initialX}vw, ${initialY}vh) translateZ(-100px)`, // Start from the back (z- direction)
            }}
          >
            {key}
            <style>
              {`
                @keyframes moveKeys${index} {
                  0% {
                    transform: translate(${initialX}vw, ${initialY}vh) translateZ(-100px) scale(0.5); // Start from the back
                    opacity: 0;
                  }
                  5% {
                    opacity: 1;
                  }
                  95% {
                    opacity: 1;
                  }
                  100% {
                    transform: translate(${initialX}vw, ${initialY}vh) translateZ(0) scale(0.5); // Move to the front
                    opacity: 0;
                  }
                }
              `}
            </style>
          </div>
        );
      });

      setCodingKeys(newKeys);
    }, 8000); // Generate new keys every 8 seconds

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
      clearInterval(generateKeysInterval);
    };
  }, []);

  return (
    <div className="coding-keys-container">
      {codingKeys}
      <style>
        {`
          .coding-keys-container {
            position: relative;
            width: 100%;
            height: 100vh;
            background-color: black;
            overflow: hidden;
          }

          .coding-key {
            position: absolute;
            color: white;
            font-size: 3rem;
          }
        `}
      </style>
    </div>
  );
};

export default CodingKeysComponent;
