import React, { useState, useEffect } from 'react';

const KeyboardKeysComponent = () => {
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
    };
  }, []);

  const key1Style = {
    fontSize: '4rem',
    color: 'white',
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: showKeys ? 'translate(-50%, -50%) translateZ(0)' : 'translate(-50%, -50%) translateZ(-1000px)',
    opacity: showKeys ? 1 : 0,
    transition: 'opacity 0.5s ease-out, transform 2s ease-out',
  };

  const key2Style = {
    fontSize: '4rem',
    color: 'white',
    position: 'absolute',
    top: '60%',
    left: '50%',
    transform: showKeys ? 'translate(-50%, -50%) translateZ(0)' : 'translate(-50%, -50%) translateZ(-1000px)',
    opacity: showKeys ? 1 : 0,
    transition: 'opacity 0.5s ease-out, transform 2s ease-out',
  };

  return (
    <div style={{ position: 'relative', width: '100%', height: '100vh', backgroundColor: 'black', overflow: 'hidden' }}>
      <div style={key1Style}>KEYBOARD</div>
      <div style={key2Style}>KEY</div>
    </div>
  );
};

export default KeyboardKeysComponent;
