import React, { useState, useEffect } from 'react';

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = ['< >', '"hello world', 'if elese', ';', "'", '?', '( )', '&', '$', '@', '#', '~' ,'&', '|', '^', '~', '<<', '>>', '>>>', '++', '--'];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey = availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: Math.random() * 100 + '%',
          top: Math.random() * 100 + '%', // Random top position within container height
          animationClass: Math.random() > 0.5 ? 'animate-forward' : 'animate-backward' // Random animation class
        };
        newKeys.push(newKey);

        // Remove key after a short delay
        setTimeout(() => {
          setKeys((prevKeys) => prevKeys.filter((k) => k.id !== newKey.id));
        }, 1500); // Adjust time as needed (2 seconds for removal)
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);
    }, 1000); // Adjust time as needed (1 second for generation)

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      position: 'relative',
      width: '100%',
      height: '100vh',
      overflow: 'hidden',
      backgroundColor: 'black', // Set black background
    }}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            position: 'absolute',
            left: key.left,
            top: key.top,
            fontSize: '24px',
            color: '#fff',
            transition: 'transform 0.5s ease', // Smooth transition for transform
            transform: key.animationClass === 'animate-forward' ? 'translateY(0)' : 'translateY(-100%)' // Apply transform based on animation class
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

export default FallingKeysComponent;
