import React, { useState, useEffect } from 'react';

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = [
    '<', '>', 'hello world', 'if else', '+', '-', '*', '/', '%', '=', '!', '<', '>', '&', '|', '^', '~', '?',
    ':', ';', ',', '.', '_', '$', '#', '@', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~', '&', '|', '^',
    '~', '<<', '>>', '>>>', '++', '--'
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey = availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: Math.random() * 100 + '%',
          top: '-10%', // Start just above the visible area
          rotation: Math.floor(Math.random() * 360), // Random rotation angle (0 to 359 degrees)
          animationDuration: `${Math.random() * 5 + 3}s`, // Random duration between 3 to 8 seconds
        };
        newKeys.push(newKey);

        // Remove key after a short delay
        setTimeout(() => {
          setKeys((prevKeys) => prevKeys.filter((k) => k.id !== newKey.id));
        }, 5000); // Random time between 5 to 10 seconds for removal
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);
    }, 1000); // Generate keys every second

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      position: 'relative',
      width: '100%',
      height: '100vh',
      overflow: 'hidden',
      backgroundColor: 'black', // Set black background
    }}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            position: 'absolute',
            left: key.left,
            top: key.top,
            fontSize: '24px',
            color: '#fff',
            transition: `top ${key.animationDuration} linear, transform ${key.animationDuration} linear`, // Smooth transitions for position and rotation
            transform: `rotate(${key.rotation}deg)` // Apply rotation
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

export default FallingKeysComponent;
