import React, { useState, useEffect } from 'react';

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = ['<', '>', '"', ':', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~'];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey = availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: Math.random() * 100 + '%',
          top: Math.random() * 100 + '%', // Random top position within container height
          animationDuration: Math.random() * 3 + 2 + 's', // Random animation duration between 2s to 5s
          animationDelay: Math.random() * 2 + 's', // Random animation delay between 0s to 2s
          transform: Math.random() > 0.5 ? 'translateY(0)' : 'translateY(-100%)' // Random forward or backward animation
        };
        newKeys.push(newKey);
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);

      // Clear keys after they fall off the screen
      setTimeout(() => {
        setKeys((prevKeys) => prevKeys.filter((key) => key.animationDelay !== '0s'));
      }, 5000); // Reduced to 5 seconds for disappearance

    }, 2000); // Reduced to 2 seconds for interval

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={containerStyle}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            ...keyStyle,
            left: key.left,
            top: key.top,
            animationDuration: key.animationDuration,
            animationDelay: key.animationDelay,
            transform: key.transform
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

// Inline styles
const containerStyle = {
  position: 'relative',
  width: '100%',
  height: '100vh',
  overflow: 'hidden',
  backgroundColor: 'black', // Set black background
};

const keyStyle = {
  position: 'absolute',
  fontSize: '24px', // Adjust font size as needed
  color: '#fff', // Set text color to white
  animationName: 'fall',
  animationTimingFunction: 'ease-in-out',
  animationIterationCount: 'infinite'
};

export default FallingKeysComponent;
