import { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Points, PointMaterial } from "@react-three/drei";

// Define positions for keyboard keys (adjust as needed)


const KeyboardKeys = (props) => {
  const ref = useRef();

  useFrame((state, delta) => {
    if (ref.current.position.z < -0.9) {
      ref.current.position.z = 0.4;
    }
    ref.current.position.z -= delta / 12; // Moving keys towards the camera (negative z-axis)
  });

  return (
    <group rotation={[0, 0, Math.PI / 4]}>
      <Points ref={ref} positions={keyboardKeysPositions.flat()} stride={3} frustumCulled {...props}>
        <PointMaterial
          transparent
          color="#ffffff"
          size={0.02} // Adjust size as needed
          sizeAttenuation={true}
          depthWrite={false}
        />
      </Points>
    </group>
  );
};

const Background = () => {
  return (
    <div className="w-full h-screen absolute inset-0 z-[-1]">
      <Canvas camera={{ position: [0, 0, 1] }}>
        <KeyboardKeys />
      </Canvas>
    </div>
  );
};

export default Background;
