import React, { useState, useEffect } from 'react';

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = [
    '<', '>', 'hello world', 'if else', '+', '-', '*', '/', '%', '=', '!', '<', '>', '&', '|', '^', '~', '?',
    ':', ';', ',', '.', '_', '$', '#', '@', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~', '&', '|', '^',
    '~', '<<', '>>', '>>>', '++', '--'
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey = availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const initialLeft = Math.random() * window.innerWidth; // Random initial horizontal position
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: initialLeft,
          top: '-10%', // Start just above the visible area
          rotation: Math.floor(Math.random() * 360), // Random rotation angle (0 to 359 degrees)
          direction: Math.random() > 0.5 ? 1 : -1, // Random direction (1 for right, -1 for left)
          speed: Math.random() * 5 + 2, // Random speed between 2 to 7 units per second
        };
        newKeys.push(newKey);

        // Remove key after a short delay
        setTimeout(() => {
          setKeys((prevKeys) => prevKeys.filter((k) => k.id !== newKey.id));
        }, Math.random() * 5000 + 5000); // Random time between 5 to 10 seconds for removal
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);
    }, 1000); // Generate keys every second

    return () => clearInterval(interval);
  }, []);

  const updatePosition = () => {
    setKeys((prevKeys) =>
      prevKeys.map((key) => {
        let newLeft = key.left + key.direction * key.speed;
        // Check if key has gone out of bounds and adjust direction
        if (newLeft < 0 || newLeft > window.innerWidth) {
          newLeft = Math.min(Math.max(newLeft, 0), window.innerWidth); // Ensure key stays within bounds
          key.direction *= -1; // Reverse direction to bounce
        }
        return { ...key, left: newLeft };
      })
    );
  };

  useEffect(() => {
    const updateInterval = setInterval(updatePosition, 50); // Update position every 50 milliseconds
    return () => clearInterval(updateInterval);
  }, []);

  return (
    <div style={{
      position: 'relative',
      width: '100%',
      height: '100vh',
      overflow: 'hidden',
      backgroundColor: 'black', // Set black background
    }}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            position: 'absolute',
            left: key.left,
            top: key.top,
            fontSize: '24px',
            color: '#fff',
            transform: `rotate(${key.rotation}deg)`, // Apply rotation
            transition: 'transform 0.1s linear', // Smooth rotation transition
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

export default FallingKeysComponent;
