import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial, Preload } from '@react-three/drei';
import * as random from 'maath/random/dist/maath-random.esm';

const Stars = (props) => {
  const starsRef = useRef();
  const [stars] = useState(() => random.inSphere(new Float32Array(5000), { radius: 1.2 }));

  useFrame((state, delta) => {
    if (starsRef.current.position.z > (0.9 + 0.1)) {
      starsRef.current.position.z = -0.4;
    }
    starsRef.current.position.z += delta / 3;
  });

  return (
    <group rotation={[0, 0, Math.PI / 4]}>
      <Points ref={starsRef} positions={stars} stride={3} frustumCulled {...props}>
        <PointMaterial
          transparent
          color='#f272c8'
          size={0.004}
          sizeAttenuation={true}
          depthWrite={false}
        />
      </Points>
    </group>
  );
};

const Background = () => {
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
    };
  }, []);

  return (
    <div className='w-full h-full absolute inset-0 z-[-1]'>
      <Canvas camera={{ position: [0, 0, 1] }}>
        <Stars />
        {showKeys && (
          <div className="keys-container">
            <div className="key1">NISSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSH</div>
            <div className="key2">B</div>
          </div>
        )}
        <Preload all />
      </Canvas>
    </div>
  );
};

export default Background;
