import React, { useState, useEffect } from 'react';

const FallingKeysComponent = () => {
  const [keys, setKeys] = useState([]);

  const availableKeys = ['<', '>', '"', ':', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~'];

  useEffect(() => {
    const interval = setInterval(() => {
      const count = Math.floor(Math.random() * 10) + 10; // Random number of keys to create
      const newKeys = [];

      for (let i = 0; i < count; i++) {
        const randomKey = availableKeys[Math.floor(Math.random() * availableKeys.length)];
        const newKey = {
          id: Date.now() + i,
          char: randomKey,
          left: Math.random() * 100 + '%',
          top: Math.random() * 100 + '%', // Random top position within container height
        };
        newKeys.push(newKey);

        // Remove key after a short delay
        setTimeout(() => {
          setKeys((prevKeys) => prevKeys.filter((k) => k.id !== newKey.id));
        }, 2000); // Adjust time as needed (2 seconds for removal)
      }

      setKeys((prevKeys) => [...prevKeys, ...newKeys]);
    }, 1000); // Adjust time as needed (1 second for generation)

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={containerStyle}>
      {keys.map((key) => (
        <div
          key={key.id}
          style={{
            ...keyStyle,
            left: key.left,
            top: key.top,
          }}
        >
          {key.char}
        </div>
      ))}
    </div>
  );
};

// Inline styles
const containerStyle = {
  position: 'relative',
  width: '100%',
  height: '100vh',
  overflow: 'hidden',
  backgroundColor: 'black', // Set black background
};

const keyStyle = {
  position: 'absolute',
  fontSize: '24px', // Adjust font size as needed
  color: '#fff', // Set text color to white
};

export default FallingKeysComponent;
