import React, { useState, useEffect } from 'react';

const CodingKeysComponent = () => {
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
    };
  }, []);

  const codingKeys = ['<', '>', '?', ':', ';', '"', '\'']; // Add more keys as needed

  const keyElements = codingKeys.map((key, index) => (
    <div key={index} className="coding-key" style={{ opacity: showKeys ? 1 : 0 }}>
      {key}
    </div>
  ));

  return (
    <div className="coding-keys-container">
      {keyElements}
      <style>
        {`
          .coding-keys-container {
            position: relative;
            width: 100%;
            height: 100vh;
            background-color: black;
            overflow: hidden;
          }

          .coding-key {
            position: absolute;
            color: white;
            font-size: 3rem;
            animation: moveKeys 4s linear infinite;
          }

          @keyframes moveKeys {
            0% {
              transform: translate3d(${randomPosition()}vw, ${randomPosition()}vh, -1000px);
              opacity: 0;
            }
            50% {
              opacity: 1;
            }
            100% {
              transform: translate3d(${randomPosition()}vw, ${randomPosition()}vh, 0);
              opacity: 0;
            }
          }

          @keyframes fadeIn {
            0% {
              opacity: 0;
            }
            100% {
              opacity: 1;
            }
          }
        `}
      </style>
    </div>
  );
};

// Function to generate random positions for animation
const randomPosition = () => Math.random() * 100 - 50; // Adjust range as needed

export default CodingKeysComponent;
