import React, { useState, useEffect } from 'react';

const CodingKeysComponent = () => {
  const [codingKeys, setCodingKeys] = useState([]);
  const [showKeys, setShowKeys] = useState(true); // Start showing keys immediately

  useEffect(() => {
    // Array of coding keys
    const keys = ['<', '>', '"', ':', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~'];

    // Function to generate random positions for animation
    const randomPosition = () => Math.random() * 100;

    // Function to generate random size for keys
    const randomSize = () => Math.random() * 3 + 1; // Random size between 1 and 4 rem

    // Generate initial set of coding keys
    const initialKeys = [];
    for (let i = 0; i < 100; i++) { // Generate 100 initial keys to fill the page
      const key = keys[Math.floor(Math.random() * keys.length)];
      const initialX = randomPosition();
      const initialY = randomPosition();
      const duration = Math.random() * 10 + 10; // Random duration between 10 to 20 seconds
      const delay = Math.random() * 4; // Random delay between 0 to 4 seconds
      const size = randomSize();

      initialKeys.push(
        <div
          key={`${key}-${i}`}
          className="coding-key"
          style={{
            opacity: showKeys ? 1 : 0,
            animation: `moveKeys${i} ${duration}s linear ${delay}s infinite forwards`, // Loop animation indefinitely
            transform: `translate(${initialX}vw, ${initialY}vh) translateZ(-1000px) scale(0)`, // Start from further back (z- direction)
            fontSize: `${size}rem`, // Random size for each key
          }}
        >
          {key}
          <style>
            {`
              @keyframes moveKeys${i} {
                0% {
                  transform: translate(${initialX}vw, ${initialY}vh) translateZ(-1000px) scale(0); // Start from the back
                  opacity: 0;
                }
                5% {
                  opacity: 1;
                  transform: translate(${initialX}vw, ${initialY}vh) translateZ(-500px) scale(1); // Move to a visible position and increase size
                }
                90% {
                  opacity: 1;
                }
                100% {
                  transform: translate(${initialX}vw, ${initialY}vh) translateZ(1000px) scale(0); // Move to the front and disappear
                  opacity: 0;
                }
              }
            `}
          </style>
        </div>
      );
    }

    setCodingKeys(initialKeys);

    // Generate new set of coding keys every 8 seconds
    const generateKeysInterval = setInterval(() => {
      const newKeys = [];
      for (let i = 0; i < 100; i++) { // Generate 100 new keys to fill the page
        const key = keys[Math.floor(Math.random() * keys.length)];
        const initialX = randomPosition();
        const initialY = randomPosition();
        const duration = Math.random() * 10 + 10; // Random duration between 10 to 20 seconds
        const delay = Math.random() * 4; // Random delay between 0 to 4 seconds
        const size = randomSize();

        newKeys.push(
          <div
            key={`${key}-${i}`}
            className="coding-key"
            style={{
              opacity: showKeys ? 1 : 0,
              animation: `moveKeys${i} ${duration}s linear ${delay}s infinite forwards`, // Loop animation indefinitely
              transform: `translate(${initialX}vw, ${initialY}vh) translateZ(-1000px) scale(0)`, // Start from further back (z- direction)
              fontSize: `${size}rem`, // Random size for each key
            }}
          >
            {key}
            <style>
              {`
                @keyframes moveKeys${i} {
                  0% {
                    transform: translate(${initialX}vw, ${initialY}vh) translateZ(-1000px) scale(0); // Start from the back
                    opacity: 0;
                  }
                  5% {
                    opacity: 1;
                    transform: translate(${initialX}vw, ${initialY}vh) translateZ(-500px) scale(1); // Move to a visible position and increase size
                  }
                  90% {
                    opacity: 1;
                  }
                  100% {
                    transform: translate(${initialX}vw, ${initialY}vh) translateZ(1000px) scale(0); // Move to the front and disappear
                    opacity: 0;
                  }
                }
              `}
            </style>
          </div>
        );
      }

      setCodingKeys(newKeys);
    }, 8000); // Generate new keys every 8 seconds

    return () => {
      clearInterval(generateKeysInterval);
    };
  }, []); // Empty dependency array to run effect once on mount

  return (
    <div className="coding-keys-container">
      {codingKeys}
      <style>
        {`
          .coding-keys-container {
            position: relative;
            width: 100%;
            height: 100vh;
            background-color: black;
            overflow: hidden;
          }

          .coding-key {
            position: absolute;
            color: white;
            font-size: 3rem;
          }
        `}
      </style>
    </div>
  );
};

export default CodingKeysComponent;
