import React, { useState, useEffect } from 'react';

const CodingKeysComponent = () => {
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
    };
  }, []);

  const codingKeys = ['<', '>', '?', ':', ';', '"', '\'']; // Add more keys as needed

  return (
    <div
      className="coding-keys-container"
      style={{
        position: 'relative',
        width: '100%',
        height: '100vh',
        backgroundColor: 'black',
        overflow: 'hidden',
      }}
    >
      {codingKeys.map((key, index) => (
        <div
          key={index}
          className="coding-key"
          style={{
            position: 'absolute',
            color: 'white',
            fontSize: '3rem',
            opacity: showKeys ? 1 : 0,
            animation: showKeys
              ? 'moveKeys 4s linear infinite'
              : 'none', // Stop animation when keys are hidden
            transformOrigin: 'center',
            transform: showKeys
              ? `translate3d(${getRandomPosition()}, ${getRandomPosition()}, -1000px)`
              : 'none', // Set random position when showing keys
          }}
        >
          {key}
        </div>
      ))}
    </div>
  );
};

const getRandomPosition = () => {
  return `${Math.random() * 2 - 1} * 100vw`;
};

export default CodingKeysComponent;
