// KeyboardBackground.js
import React, { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Points, PointMaterial } from "@react-three/drei";

const KeyboardBackground = () => {
  const ref = useRef();

  // Define positions for keyboard keys (adjust as needed)
  const keyboardKeysPositions = [
    [0, 0, -5],   // Example position for key 1
    [0.2, 0, -5], // Example position for key 2
    [-0.2, 0, -5], // Example position for key 3
    // Add more positions for other keys as needed
  ];

  return (
    <Canvas camera={{ position: [0, 0, 1] }}>
      <ambientLight intensity={0.5} />
      <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} />
      <Points ref={ref} positions={keyboardKeysPositions.flat()} stride={3} frustumCulled>
        <PointMaterial
          transparent
          color="#ffffff"
          size={0.1} // Adjust size as needed
          sizeAttenuation={true}
          depthWrite={false}
        />
      </Points>
      <KeyboardAnimation ref={ref} />
    </Canvas>
  );
};

const KeyboardAnimation = ({ ref }) => {
  useFrame(() => {
    if (ref.current.position.z > 0.9) {
      ref.current.position.z = -5;
    }
    ref.current.position.z += 0.005; // Adjust speed as needed
  });

  return null;
};

export default KeyboardBackground;
