import { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Suspense } from "react";
import { Box, Preload } from "@react-three/drei";

const RotatingCube = () => {
  const boxRotation = useRef();

  // Custom hook to rotate the cube
  useFrame(({ clock }) => {
    // Rotate the cube on the x and y axes
    if (boxRotation.current) {
      boxRotation.current.rotation.x = Math.sin(clock.elapsedTime) * Math.PI * 0.1;
      boxRotation.current.rotation.y = Math.cos(clock.elapsedTime) * Math.PI * 0.1;
    }
  });

  return (
    <group>
      <Box ref={boxRotation} args={[1, 1, 1]} position={[0, 0, 0]} material-color="#f272c8" />
    </group>
  );
};

const Background = () => {
  return (
    <div className="w-full h-screen absolute inset-0 z-[-1]">
      <Canvas camera={{ position: [0, 0, 5] }}>
        <Suspense fallback={null}>
          <RotatingCube />
        </Suspense>
        <Preload all />
      </Canvas>
    </div>
  );
};

export default Background;
