import React, { useState, useEffect } from 'react';

const CodingKeysComponent = () => {
  const [showKeys, setShowKeys] = useState(false);

  useEffect(() => {
    // Show keys after 2 seconds
    const showKeysTimeout = setTimeout(() => setShowKeys(true), 2000);

    // Hide keys after 6 seconds
    const hideKeysTimeout = setTimeout(() => setShowKeys(false), 6000);

    return () => {
      clearTimeout(showKeysTimeout);
      clearTimeout(hideKeysTimeout);
    };
  }, []);

  const codingKeys = ['<', '>', '"', ':', ';', "'", '?', '(', ')', '&', '$', '@', '#', '~'];

  const keyElements = codingKeys.map((key, index) => {
    // Generate random initial positions and animation durations
    const initialX = randomPosition();
    const initialY = randomPosition();
    const duration = Math.random() * 8 + 4; // Random duration between 4 to 12 seconds
    const delay = Math.random() * 4; // Random delay between 0 to 4 seconds

    return (
      <div
        key={index}
        className="coding-key"
        style={{
          opacity: showKeys ? 1 : 0,
          animation: `moveKeys${index} ${duration}s linear ${delay}s infinite`,
          transform: `translate(${initialX}vw, ${initialY}vh)`, // Random start position
        }}
      >
        {key}
        <style>
          {`
            @keyframes moveKeys${index} {
              0% {
                transform: translate(${initialX}vw, ${initialY}vh) scale(0.5);
                opacity: 0;
              }
              50% {
                transform: translate(${initialX}vw, ${initialY}vh) scale(1.2);
                opacity: 1;
              }
              100% {
                transform: translate(${initialX}vw, ${initialY}vh) scale(0.5);
                opacity: 0;
              }
            }
          `}
        </style>
      </div>
    );
  });

  return (
    <div className="coding-keys-container">
      {keyElements}
      <style>
        {`
          .coding-keys-container {
            position: relative;
            width: 100%;
            height: 100vh;
            background-color: black;
            overflow: hidden;
          }

          .coding-key {
            position: absolute;
            color: white;
            font-size: 3rem;
          }
        `}
      </style>
    </div>
  );
};

// Function to generate random positions for animation
const randomPosition = () => Math.random() * 100; // Adjust range as needed

export default CodingKeysComponent;
